public interface FfiBidirectionalStreamInterface {
    
    /**
     * Signal that writing is complete and close the stream.
     *
     * This sends a FIN to the peer indicating no more data will be written.
     * The stream object can still be used to read remaining data.
     * Resources are fully released when the object is dropped.
     */
    fun `finish`()
    
    fun `peerIdentity`(): kotlin.String
    
    fun `read`(`maxBytes`: kotlin.UInt): kotlin.ByteArray
    
    fun `readExact`(`nBytes`: kotlin.UInt): kotlin.ByteArray
    
    fun `streamId`(): kotlin.ULong
    
    fun `write`(`data`: kotlin.ByteArray)
    
    companion object
}

open class FfiBidirectionalStream: Disposable, AutoCloseable, FfiBidirectionalStreamInterface {

    constructor(pointer: Pointer) {
        this.pointer = pointer
        this.cleanable = UniffiLib.CLEANER.register(this, UniffiCleanAction(pointer))
    }

    /**
     * This constructor can be used to instantiate a fake object. Only used for tests. Any
     * attempt to actually use an object constructed this way will fail as there is no
     * connected Rust object.
     */
    @Suppress("UNUSED_PARAMETER")
    constructor(noPointer: NoPointer) {
        this.pointer = null
        this.cleanable = UniffiLib.CLEANER.register(this, UniffiCleanAction(pointer))
    }

    protected val pointer: Pointer?
    protected val cleanable: UniffiCleaner.Cleanable

    private val wasDestroyed = AtomicBoolean(false)
    private val callCounter = AtomicLong(1)

    override fun destroy() {
        // Only allow a single call to this method.
        // TODO: maybe we should log a warning if called more than once?
        if (this.wasDestroyed.compareAndSet(false, true)) {
            // This decrement always matches the initial count of 1 given at creation time.
            if (this.callCounter.decrementAndGet() == 0L) {
                cleanable.clean()
            }
        }
    }

    @Synchronized
    override fun close() {
        this.destroy()
    }

    internal inline fun <R> callWithPointer(block: (ptr: Pointer) -> R): R {
        // Check and increment the call counter, to keep the object alive.
        // This needs a compare-and-set retry loop in case of concurrent updates.
        do {
            val c = this.callCounter.get()
            if (c == 0L) {
                throw IllegalStateException("${this.javaClass.simpleName} object has already been destroyed")
            }
            if (c == Long.MAX_VALUE) {
                throw IllegalStateException("${this.javaClass.simpleName} call counter would overflow")
            }
        } while (! this.callCounter.compareAndSet(c, c + 1L))
        // Now we can safely do the method call without the pointer being freed concurrently.
        try {
            return block(this.uniffiClonePointer())
        } finally {
            // This decrement always matches the increment we performed above.
            if (this.callCounter.decrementAndGet() == 0L) {
                cleanable.clean()
            }
        }
    }

    // Use a static inner class instead of a closure so as not to accidentally
    // capture `this` as part of the cleanable's action.
    private class UniffiCleanAction(private val pointer: Pointer?) : Runnable {
        override fun run() {
            pointer?.let { ptr ->
                uniffiRustCall { status ->
                    UniffiLib.INSTANCE.uniffi_korium_fn_free_ffibidirectionalstream(ptr, status)
                }
            }
        }
    }

    fun uniffiClonePointer(): Pointer {
        return uniffiRustCall() { status ->
            UniffiLib.INSTANCE.uniffi_korium_fn_clone_ffibidirectionalstream(pointer!!, status)
        }
    }

    
    /**
     * Signal that writing is complete and close the stream.
     *
     * This sends a FIN to the peer indicating no more data will be written.
     * The stream object can still be used to read remaining data.
     * Resources are fully released when the object is dropped.
     */
    @Throws(KoriumException::class)override fun `finish`()
        = 
    callWithPointer {
    uniffiRustCallWithError(KoriumException) { _status ->
    UniffiLib.INSTANCE.uniffi_korium_fn_method_ffibidirectionalstream_finish(
        it, _status)
}
    }
    
    

    override fun `peerIdentity`(): kotlin.String {
            return FfiConverterString.lift(
    callWithPointer {
    uniffiRustCall() { _status ->
    UniffiLib.INSTANCE.uniffi_korium_fn_method_ffibidirectionalstream_peer_identity(
        it, _status)
}
    }
    )
    }
    

    
    @Throws(KoriumException::class)override fun `read`(`maxBytes`: kotlin.UInt): kotlin.ByteArray {
            return FfiConverterByteArray.lift(
    callWithPointer {
    uniffiRustCallWithError(KoriumException) { _status ->
    UniffiLib.INSTANCE.uniffi_korium_fn_method_ffibidirectionalstream_read(
        it, FfiConverterUInt.lower(`maxBytes`),_status)
}
    }
    )
    }
    

    
    @Throws(KoriumException::class)override fun `readExact`(`nBytes`: kotlin.UInt): kotlin.ByteArray {
            return FfiConverterByteArray.lift(
    callWithPointer {
    uniffiRustCallWithError(KoriumException) { _status ->
    UniffiLib.INSTANCE.uniffi_korium_fn_method_ffibidirectionalstream_read_exact(
        it, FfiConverterUInt.lower(`nBytes`),_status)
