public object FfiConverterTypeFfiBidirectionalStream: FfiConverter<FfiBidirectionalStream, Pointer> {

    override fun lower(value: FfiBidirectionalStream): Pointer {
        return value.uniffiClonePointer()
    }

    override fun lift(value: Pointer): FfiBidirectionalStream {
        return FfiBidirectionalStream(value)
    }

    override fun read(buf: ByteBuffer): FfiBidirectionalStream {
        // The Rust code always writes pointers as 8 bytes, and will
        // fail to compile if they don't fit.
        return lift(Pointer(buf.getLong()))
    }

    override fun allocationSize(value: FfiBidirectionalStream) = 8UL

    override fun write(value: FfiBidirectionalStream, buf: ByteBuffer) {
        // The Rust code always expects pointers written as 8 bytes,
        // and will fail to compile if they don't fit.
        buf.putLong(Pointer.nativeValue(lower(value)))
    }
}


