 */
public object FfiConverterTypeFfiNode: FfiConverter<FfiNode, Pointer> {

    override fun lower(value: FfiNode): Pointer {
        return value.uniffiClonePointer()
    }

    override fun lift(value: Pointer): FfiNode {
        return FfiNode(value)
    }

    override fun read(buf: ByteBuffer): FfiNode {
        // The Rust code always writes pointers as 8 bytes, and will
        // fail to compile if they don't fit.
        return lift(Pointer(buf.getLong()))
    }

    override fun allocationSize(value: FfiNode) = 8UL

    override fun write(value: FfiNode, buf: ByteBuffer) {
        // The Rust code always expects pointers written as 8 bytes,
