/**
 * @suppress
 */
public object FfiConverterTypeKoriumError : FfiConverterRustBuffer<KoriumException> {
    override fun read(buf: ByteBuffer): KoriumException {
        

        return when(buf.getInt()) {
            1 -> KoriumException.InvalidAddress()
            2 -> KoriumException.InvalidIdentity()
            3 -> KoriumException.InvalidKey()
            4 -> KoriumException.ConnectionFailed()
            5 -> KoriumException.Timeout()
            6 -> KoriumException.NotConnected()
            7 -> KoriumException.AlreadySubscribed()
            8 -> KoriumException.NotSubscribed()
            9 -> KoriumException.ChannelClosed()
            10 -> KoriumException.PowFailed()
            11 -> KoriumException.NotFound()
            12 -> KoriumException.InvalidSignature()
            13 -> KoriumException.CaConfigInvalid(
                FfiConverterString.read(buf),
                )
            14 -> KoriumException.DkgFailed(
                FfiConverterString.read(buf),
                )
            15 -> KoriumException.SigningFailed(
                FfiConverterString.read(buf),
                )
            16 -> KoriumException.InsufficientSigners(
                FfiConverterUShort.read(buf),
                FfiConverterUShort.read(buf),
                )
            17 -> KoriumException.NotASigner()
            18 -> KoriumException.SerializationException(
                FfiConverterString.read(buf),
                )
            19 -> KoriumException.CertificateException(
                FfiConverterString.read(buf),
                )
            20 -> KoriumException.Internal(
                FfiConverterString.read(buf),
                )
            else -> throw RuntimeException("invalid error enum value, something is very wrong!!")
        }
    }

    override fun allocationSize(value: KoriumException): ULong {
        return when(value) {
            is KoriumException.InvalidAddress -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
            )
            is KoriumException.InvalidIdentity -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
            )
            is KoriumException.InvalidKey -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
            )
            is KoriumException.ConnectionFailed -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
            )
            is KoriumException.Timeout -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
            )
            is KoriumException.NotConnected -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
            )
            is KoriumException.AlreadySubscribed -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
            )
            is KoriumException.NotSubscribed -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
            )
            is KoriumException.ChannelClosed -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
            )
            is KoriumException.PowFailed -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
            )
            is KoriumException.NotFound -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
            )
            is KoriumException.InvalidSignature -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
            )
            is KoriumException.CaConfigInvalid -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
                + FfiConverterString.allocationSize(value.`msg`)
            )
            is KoriumException.DkgFailed -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
                + FfiConverterString.allocationSize(value.`msg`)
            )
            is KoriumException.SigningFailed -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
                + FfiConverterString.allocationSize(value.`msg`)
            )
            is KoriumException.InsufficientSigners -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
                + FfiConverterUShort.allocationSize(value.`required`)
                + FfiConverterUShort.allocationSize(value.`received`)
            )
            is KoriumException.NotASigner -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
            )
            is KoriumException.SerializationException -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
                + FfiConverterString.allocationSize(value.`msg`)
            )
