/**
 * The main Korium node interface for mobile.
 */
public protocol KoriumNodeProtocol : AnyObject {
    
    /**
     * Add a peer to the routing table.
     */
    func addPeer(identityHex: String, addresses: [String]) throws 
    
    /**
     * Bootstrap by connecting to a known peer.
     */
    func bootstrap(identityHex: String, addresses: [String]) throws 
    
    /**
     * Bootstrap using public seed nodes.
     */
    func bootstrapPublic() throws 
    
    /**
     * Get this node's identity (hex-encoded public key).
     */
    func identity()  -> String
    
    /**
     * Get the local bind address.
     */
    func localAddress() throws  -> String
    
    /**
     * Poll for next received message (non-blocking, returns None if none available).
     */
    func pollMessage() throws  -> PubSubMessage?
    
    /**
     * Publish to a PubSub topic.
     */
    func publish(topic: String, data: Data) throws 
    
    /**
     * Resolve an identity to contact information.
     */
    func resolve(identityHex: String) throws  -> PeerContact?
    
    /**
     * Get routable addresses for this node.
     */
    func routableAddresses()  -> [String]
    
    /**
     * Send a request and wait for response.
     */
    func send(identityHex: String, request: Data) throws  -> Data
    
    /**
     * Shutdown the node.
     */
    func shutdown() 
    
    /**
     * Subscribe to a PubSub topic.
     */
    func subscribe(topic: String) throws 
    
    /**
     * Get list of subscribed topics.
     */
    func subscriptions() throws  -> [String]
    
    /**
     * Get telemetry snapshot.
     */
    func telemetry() throws  -> Telemetry
    
    /**
     * Unsubscribe from a PubSub topic.
     */
    func unsubscribe(topic: String) throws 
    
}

