/**
 * FFI-safe error type.
 */
public enum KoriumError {

    
    
    case InvalidAddress
    case InvalidIdentity
    case ConnectionFailed
    case Timeout
    case NotConnected
    case AlreadySubscribed
    case NotSubscribed
    case ChannelClosed
    case PowFailed
    case Internal(msg: String
    )
}


#if swift(>=5.8)
@_documentation(visibility: private)
#endif
public struct FfiConverterTypeKoriumError: FfiConverterRustBuffer {
    typealias SwiftType = KoriumError

    public static func read(from buf: inout (data: Data, offset: Data.Index)) throws -> KoriumError {
        let variant: Int32 = try readInt(&buf)
        switch variant {

        

        
        case 1: return .InvalidAddress
        case 2: return .InvalidIdentity
        case 3: return .ConnectionFailed
        case 4: return .Timeout
        case 5: return .NotConnected
        case 6: return .AlreadySubscribed
        case 7: return .NotSubscribed
        case 8: return .ChannelClosed
        case 9: return .PowFailed
        case 10: return .Internal(
            msg: try FfiConverterString.read(from: &buf)
            )

         default: throw UniffiInternalError.unexpectedEnumCase
        }
    }

    public static func write(_ value: KoriumError, into buf: inout [UInt8]) {
        switch value {

        

        
        
        case .InvalidAddress:
            writeInt(&buf, Int32(1))
        
        
        case .InvalidIdentity:
            writeInt(&buf, Int32(2))
        
        
        case .ConnectionFailed:
            writeInt(&buf, Int32(3))
        
        
        case .Timeout:
            writeInt(&buf, Int32(4))
        
        
        case .NotConnected:
            writeInt(&buf, Int32(5))
        
        
        case .AlreadySubscribed:
            writeInt(&buf, Int32(6))
        
        
        case .NotSubscribed:
            writeInt(&buf, Int32(7))
        
        
        case .ChannelClosed:
            writeInt(&buf, Int32(8))
        
        
        case .PowFailed:
            writeInt(&buf, Int32(9))
        
        
        case let .Internal(msg):
            writeInt(&buf, Int32(10))
            FfiConverterString.write(msg, into: &buf)
            
        }
    }
}


